import { Component, OnInit } from '@angular/core';
import { DataService } from './services/data.service';
import { DatabaseSchema } from './models/data.models';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  currentView: string = 'employes';
  showModal = false;
  editId: number | null = null;
  db: DatabaseSchema | null = null;

  columns: Record<string, string[]> = {
    employes: ['id', 'email', 'adresse', 'telephone', 'nom', 'prenom', 'cin', 'dateNaissance', 'poste', 'salaire'],
    contrats: ['id', 'type', 'employe', 'dateDebut', 'dateFin'],
    presences: ['id', 'employe', 'date', 'status'],
    conges: ['id', 'employe', 'type', 'debut', 'fin'],
    paie: ['id', 'employe', 'mois', 'montant'],
    formations: ['id', 'titre', 'employe', 'date']
  };

  items: any[] = [];

  constructor(private dataService: DataService) {}

  ngOnInit(): void {
    this.dataService.db$.subscribe(db => {
      this.db = db;
      this.updateItems();
    });
  }

  showView(view: string): void {
    this.currentView = view;
    this.updateItems();
  }

  private updateItems(): void {
    if (!this.db) return;
    const section = this.currentView as keyof DatabaseSchema;
    this.items = this.db[section] || [];
  }

  openForm(id: number | null = null): void {
    this.editId = id;
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.editId = null;
  }

  capitalize(str: string): string {
    if (!str) return '';
    return str.charAt(0).toUpperCase() + str.slice(1);
  }
}
<header class="header">
  <div class="brand">
    <img src="https://cdn-icons-png.flaticon.com/512/2966/2966481.png" alt="logo">
    CliniqueCare – Gestion RH Médicale
  </div>
</header>

<div class="app">
  <nav>
    <button 
      [class.active]="currentView === 'employes'" 
      (click)="showView('employes')">
      <i class="fa-solid fa-user-doctor"></i> Employés
    </button>
    <button 
      [class.active]="currentView === 'contrats'" 
      (click)="showView('contrats')">
      <i class="fa-solid fa-file-contract"></i> Contrats
    </button>
    <button 
      [class.active]="currentView === 'presences'" 
      (click)="showView('presences')">
      <i class="fa-solid fa-clipboard-check"></i> Présences
    </button>
    <button 
      [class.active]="currentView === 'conges'" 
      (click)="showView('conges')">
      <i class="fa-solid fa-umbrella-beach"></i> Congés
    </button>
    <button 
      [class.active]="currentView === 'paie'" 
      (click)="showView('paie')">
      <i class="fa-solid fa-sack-dollar"></i> Paie
    </button>
    <button 
      [class.active]="currentView === 'formations'" 
      (click)="showView('formations')">
      <i class="fa-solid fa-graduation-cap"></i> Formations
    </button>
  </nav>

  <main id="content">
    <app-table 
      [currentView]="currentView" 
      [items]="items" 
      [columns]="columns[currentView]"
      (openForm)="openForm($event)">
    </app-table>
  </main>
</div>

<!-- Modal -->
<app-modal 
  [showModal]="showModal" 
  [currentView]="currentView" 
  [editId]="editId"
  (closeModal)="closeModal()"
  (itemSaved)="closeModal(); updateItems()">
</app-modal>
