import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { DataService } from '../../services/data.service';
import { DatabaseSchema } from '../../models/data.models';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent implements OnInit {
  @Input() showModal = false;
  @Input() currentView: string = '';
  @Input() editId: number | null = null;
  @Output() closeModal = new EventEmitter<void>();
  @Output() itemSaved = new EventEmitter<void>();

  formData: Record<string, string> = {};
  db: DatabaseSchema | null = null;

  constructor(private dataService: DataService) {}

  ngOnInit(): void {
    this.dataService.db$.subscribe((db: DatabaseSchema) => {
      this.db = db;
    });
  }

  ngOnChanges(): void {
    if (this.showModal && this.editId) {
      this.loadItemData();
    } else if (this.showModal) {
      this.formData = {};
    }
  }

  private loadItemData(): void {
    if (!this.db || !this.editId) return;
    const section = this.currentView as keyof DatabaseSchema;
    const items = this.db[section] as any[];
    const item = items.find(x => x.id === this.editId);
    if (item) {
      this.formData = { ...item };
    }
  }

  onCloseModal(): void {
    this.closeModal.emit();
  }

  onSave(): void {
    // Validation
    if (this.currentView === 'employes' && !(this.formData['nom'] || this.formData['prenom'])) {
      Swal.fire('Erreur', 'Le nom ou le prénom est requis pour un employé.', 'error');
      return;
    }

    // Ensure currency format for salary fields
    if (this.formData['salaire'] && !/tnd/i.test(this.formData['salaire'])) {
      this.formData['salaire'] = this.formData['salaire'].trim() + ' TND';
    }
    if (this.formData['montant'] && !/tnd/i.test(this.formData['montant'])) {
      this.formData['montant'] = this.formData['montant'].trim() + ' TND';
    }

    const section = this.currentView as keyof DatabaseSchema;

    if (this.editId) {
      this.dataService.updateItem(section, this.editId, this.formData);
      this.showUpdateSuccess(this.capitalize(this.currentView));
    } else {
      const newItem = {
        id: Date.now(),
        ...this.formData
      };
      this.dataService.addItem(section, newItem);
      this.showAddSuccess(this.capitalize(this.currentView));
    }

    this.closeModal.emit();
    this.itemSaved.emit();
  }

  capitalize(str: string): string {
    if (!str) return '';
    return str.charAt(0).toUpperCase() + str.slice(1);
  }

  private showAddSuccess(entity: string): void {
    Swal.fire({
      toast: true,
      position: 'top-end',
      icon: 'success',
      title: `${entity} ajouté`,
      showConfirmButton: false,
      timer: 1500
    });
  }

  private showUpdateSuccess(entity: string): void {
    Swal.fire({
      toast: true,
      position: 'top-end',
      icon: 'info',
      title: `${entity} mis à jour`,
      showConfirmButton: false,
      timer: 1500
    });
  }
}
<div class="modal-bg" [class.show]="showModal">
  <div class="modal" *ngIf="showModal">
    <h4>{{ editId ? 'Modifier' : 'Ajouter' }} {{ capitalize(currentView) }}</h4>

    <!-- Employees Form -->
    <ng-container *ngIf="currentView === 'employes'">
      <label>Email</label>
      <input [(ngModel)]="formData.email" placeholder="Email">
      
      <label>Adresse</label>
      <input [(ngModel)]="formData.adresse" placeholder="Adresse">
      
      <label>Téléphone</label>
      <input [(ngModel)]="formData.telephone" placeholder="Téléphone">
      
      <label>Nom</label>
      <input [(ngModel)]="formData.nom" placeholder="Nom">
      
      <label>Prénom</label>
      <input [(ngModel)]="formData.prenom" placeholder="Prénom">
      
      <label>CIN</label>
      <input [(ngModel)]="formData.cin" placeholder="CIN">
      
      <label>Date de naissance</label>
      <input type="date" [(ngModel)]="formData.dateNaissance">
      
      <label>Poste</label>
      <input [(ngModel)]="formData.poste" placeholder="Poste">
      
      <label>Salaire</label>
      <input [(ngModel)]="formData.salaire" placeholder="0 TND">
    </ng-container>

    <!-- Contracts Form -->
    <ng-container *ngIf="currentView === 'contrats'">
      <label>Type (CDD / CDI)</label>
      <input [(ngModel)]="formData.type" placeholder="CDI">
      
      <label>Employé</label>
      <input [(ngModel)]="formData.employe" placeholder="Employé">
      
      <label>Date Début</label>
      <input type="date" [(ngModel)]="formData.dateDebut">
      
      <label>Date Fin</label>
      <input type="date" [(ngModel)]="formData.dateFin">
    </ng-container>

    <!-- Presences Form -->
    <ng-container *ngIf="currentView === 'presences'">
      <label>Employé</label>
      <input [(ngModel)]="formData.employe" placeholder="Employé">
      
      <label>Date</label>
      <input type="date" [(ngModel)]="formData.date">
      
      <label>Status</label>
      <select [(ngModel)]="formData.status">
        <option value="Présent">Présent</option>
        <option value="Absent">Absent</option>
      </select>
    </ng-container>

    <!-- Leaves Form -->
    <ng-container *ngIf="currentView === 'conges'">
      <label>Employé</label>
      <input [(ngModel)]="formData.employe" placeholder="Employé">
      
      <label>Type</label>
      <select [(ngModel)]="formData.type">
        <option value="Annuel">Annuel</option>
        <option value="Maladie">Maladie</option>
      </select>
      
      <label>Début</label>
      <input type="date" [(ngModel)]="formData.debut">
      
      <label>Fin</label>
      <input type="date" [(ngModel)]="formData.fin">
    </ng-container>

    <!-- Payroll Form -->
    <ng-container *ngIf="currentView === 'paie'">
      <label>Employé</label>
      <input [(ngModel)]="formData.employe" placeholder="Employé">
      
      <label>Mois</label>
      <input [(ngModel)]="formData.mois" placeholder="Mois">
      
      <label>Montant</label>
      <input [(ngModel)]="formData.montant" placeholder="Montant">
    </ng-container>

    <!-- Training Form -->
    <ng-container *ngIf="currentView === 'formations'">
      <label>Titre</label>
      <input [(ngModel)]="formData.titre" placeholder="Titre">
      
      <label>Employé</label>
      <input [(ngModel)]="formData.employe" placeholder="Employé">
      
      <label>Date</label>
      <input type="date" [(ngModel)]="formData.date">
    </ng-container>

    <div class="btns">
      <button class="btn-cancel" (click)="onCloseModal()">Annuler</button>
      <button class="btn-save" (click)="onSave()">Enregistrer</button>
    </div>
  </div>
</div>
